import React, { Fragment, useState } from "react";

const AddNewCustomer = () =>{
    const [customerNo, setCustomerNo] = useState('');

    const onSubmitForm = async (e) =>
    {
        e.preventDefault();
        try {
            const body = {customerNo};
            const response = fetch("http://localhost:5000/customer")
        } catch (error) {
            console.log(error.message);
        }
    }
    
    return (
    <Fragment>
    <h1 className = "text-center mt-5">Add Customer</h1>
    <form className = ' mt-5'>

        <h5 className = 'd-flex mt-5'>Customer No
        <input 
            type = 'text'
            className = 'form-control'
            value = {customerNo}
            onChange = {e => setCustomerNo(e.target.value)}/>
        </h5>
        
        <h5 className = 'd-flex'>Surname
        <input type = 'text'/>
        </h5>

        <h5 className = 'd-flex'>Given Name
        <input type = 'text'/>
        </h5>
        
        <h5 className = 'd-flex'>Unit No.
        <input type = 'text'/>
        </h5>

        <h5 className = 'd-flex'>Street 
        <input type = 'text'/>
        </h5>
        
        <h5 className = '\n d-flex'>City
        <input type = 'text'/>
        </h5>

        <h5 className = 'd-flex'>Phone Number
        <input type = 'text'/>
        </h5>
        
        <h5 className = 'd-flex'>Represented
        <input type = 'text'/>
        </h5>

        <h5 className = 'd-flex'>Customer No
        <input type = 'text'/>
        </h5>
        
        <h5 className = 'd-flex'>Rep Name
        <input type = 'text'/>
        </h5>
       
        <button className = 'btn btn-success mt-5'>Add</button>
    </form>
    
    </Fragment>
    );
    
    
}

export default AddNewCustomer;
