const AddNewCustomer = () =>{

    
    return (
    <Fragment>
    <h1 className = "text-center mt-5">Add Customer</h1>
    <form className = 'd-flex mt-5'>
        <h3>Customer No
        <input type = 'text'/>
        </h3>
        
        <h3>Surname
        <input type = 'text'/>
        </h3>
        <button className = 'btn btn-success'>Add</button>
    </form>
    </Fragment>
    );
    
    
}

export default AddNewCustomer;
