import React, { Fragment, useState } from "react";

const AddNewCustomer = () =>{
    const [customerNo, setCustomerNo] = useState('');

    const onSubmitForm = async (e) =>
    {
        e.preventDefault();
        try {
            const body = {customerNo};
            const response = await fetch("localhost:5000/customer",{
                method: "POST",
                headers: {"Content-Type": "application/json"},
                body: JSON.stringify(body)
            });

            console.log(response);
        } catch (error) {
            console.log(error.message);
        }
    }
    
    return (
    <Fragment>
    <h1 className = "text-center mt-5">Add Customer</h1>
    <form className = 'mt-5' onSubmit = {onSubmitForm}>

        <h5 className = 'd-flex mt-5'>Customer No
        <input 
            type = 'text'
            className = 'form-control'
            value = {customerNo}
            onChange = {e => setCustomerNo(e.target.value)}/>
        </h5>
        
        
        <button className = 'btn btn-success mt-5'>Add</button>
    </form>
    
    </Fragment>
    );
    
    
}

export default AddNewCustomer;
